"use strict";(self.webpackChunkwaku_guide=self.webpackChunkwaku_guide||[]).push([[53],{1109:e=>{e.exports=JSON.parse('{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"main":[{"type":"category","label":"About Waku","collapsible":false,"items":[{"type":"link","label":"What is Waku?","href":"/","docId":"overview/index"},{"type":"link","label":"History of Waku","href":"/overview/history","docId":"overview/history"},{"type":"link","label":"Why Waku?","href":"/overview/why-waku","docId":"overview/why-waku"},{"type":"link","label":"Use Cases","href":"/overview/use-cases","docId":"overview/use-cases"},{"type":"html","value":"<a href=\'/guides/nodes-and-sdks\' target=\'_blank\' rel=\'noopener noreferrer\' class=\'menu__link external-link\'>Nodes and SDKs <svg width=\'13.5\' height=\'13.5\' viewBox=\'0 0 24 24\'><path fill=\'currentColor\' d=\'M21 13v10h-21v-19h12v2h-10v15h17v-8h2zm3-12h-10.988l4.035 4-6.977 7.07 2.828 2.828 6.977-7.07 4.125 4.172v-11z\'></path></svg></a>"},{"type":"html","value":"<a href=\'/presentations\' target=\'_blank\' rel=\'noopener noreferrer\' class=\'menu__link external-link\'>Presentations <svg width=\'13.5\' height=\'13.5\' viewBox=\'0 0 24 24\'><path fill=\'currentColor\' d=\'M21 13v10h-21v-19h12v2h-10v15h17v-8h2zm3-12h-10.988l4.035 4-6.977 7.07 2.828 2.828 6.977-7.07 4.125 4.172v-11z\'></path></svg></a>"},{"type":"category","label":"Concepts","collapsed":false,"items":[{"type":"link","label":"Protocols","href":"/overview/concepts/protocols","docId":"overview/concepts/protocols"},{"type":"link","label":"Content Topics","href":"/overview/concepts/content-topics","docId":"overview/concepts/content-topics"},{"type":"link","label":"Network Domains","href":"/overview/concepts/network-domains","docId":"overview/concepts/network-domains"},{"type":"category","label":"Peer Discovery","items":[{"type":"link","label":"Predefined Nodes","href":"/overview/concepts/peer-discovery/predefined-nodes","docId":"overview/concepts/peer-discovery/predefined-nodes"},{"type":"link","label":"DNS Discovery","href":"/overview/concepts/peer-discovery/dns-discovery","docId":"overview/concepts/peer-discovery/dns-discovery"},{"type":"link","label":"Discv5","href":"/overview/concepts/peer-discovery/discv5","docId":"overview/concepts/peer-discovery/discv5"},{"type":"link","label":"Peer Exchange","href":"/overview/concepts/peer-discovery/peer-exchange","docId":"overview/concepts/peer-discovery/peer-exchange"}],"collapsed":true,"collapsible":true,"href":"/overview/concepts/peer-discovery"},{"type":"link","label":"Transports","href":"/overview/concepts/transports","docId":"overview/concepts/transports"}],"collapsible":true},{"type":"category","label":"Reference","items":[{"type":"link","label":"Glossary","href":"/overview/reference/glossary","docId":"overview/reference/glossary"},{"type":"link","label":"Security Features","href":"/overview/reference/security-features","docId":"overview/reference/security-features"},{"type":"link","label":"Research in Progress","href":"/overview/reference/research-in-progress","docId":"overview/reference/research-in-progress"},{"type":"link","label":"Comparing Waku and libp2p","href":"/overview/reference/waku-vs-libp2p","docId":"overview/reference/waku-vs-libp2p"}],"collapsed":true,"collapsible":true}],"collapsed":false}],"guides":[{"type":"category","label":"Guides","collapsible":false,"items":[{"type":"link","label":"Nodes and SDKs","href":"/guides/nodes-and-sdks","docId":"guides/nodes-and-sdks"},{"type":"link","label":"Run a Nwaku Node","href":"/guides/run-nwaku-node","docId":"guides/run-nwaku-node"}],"collapsed":false}],"community":[{"type":"category","label":"Community","collapsible":false,"items":[{"type":"link","label":"Powered by Waku","href":"/powered-by-waku","docId":"powered-by-waku"},{"type":"link","label":"Join Our Community","href":"/community","docId":"community"},{"type":"link","label":"Contribute to Waku","href":"/contribute","docId":"contribute"},{"type":"link","label":"Watch Our Presentations","href":"/presentations","docId":"presentations"},{"type":"link","label":"Bug Bounty Program","href":"https://hackenproof.com/status-dot-im/waku"}],"collapsed":false}]},"docs":{"clients/js-waku/build-chat-app":{"id":"clients/js-waku/build-chat-app","title":"Build a Chat App","description":"In this guide, you will learn how to receive and send messages using Waku by building an app from scratch."},"clients/js-waku/index":{"id":"clients/js-waku/index","title":"Introduction","description":"JS-Waku is the TypeScript implementation of the Waku protocol,"},"clients/js-waku/quick-start":{"id":"clients/js-waku/quick-start","title":"Quick Start","description":"In this guide, you will learn how to integrate Waku into an existing JavaScript project."},"community":{"id":"community","title":"Join Our Community","description":"Welcome to the Waku Community! Whether you are interested in building with Waku, contributing to the network, expanding your knowledge, or staying up-to-date with our progress, we have something for everyone.","sidebar":"community"},"contribute":{"id":"contribute","title":"Contribute to Waku","description":"Get involved in Waku\'s open-source initiatives to improve the protocols, SDKs, developer tools and examples, and documentation. You can contribute by:","sidebar":"community"},"guides/nodes-and-sdks":{"id":"guides/nodes-and-sdks","title":"Nodes and SDKs","description":"Waku has risks and limitations as it is still developing and preparing for extensive adoption. However, it is already demonstrating its capabilities by powering various applications. Join our community to stay updated on our progress.","sidebar":"guides"},"guides/run-nwaku-node":{"id":"guides/run-nwaku-node","title":"Run a Nwaku Node","description":"Nwaku (formerly nim-waku) is a lightweight and robust Nim client for running a Waku node, equipped with tools to monitor and maintain a running node. Nwaku is highly configurable, enabling operators to select the protocols they wish to support based on their needs, motivations, and available resources.","sidebar":"guides"},"overview/concepts/content-topics":{"id":"overview/concepts/content-topics","title":"Content Topics","description":"Content Topics are metadata strings set by developers on outgoing messages to facilitate protocol-level features like selectively processing incoming messages (Relay or Filter) and retrieving historical messages (Store) that meet specific filtering criteria. Please refer to the WAKU2-TOPICS specification to learn more.","sidebar":"main"},"overview/concepts/network-domains":{"id":"overview/concepts/network-domains","title":"Network Domains","description":"Waku is a unified and cohesive entity that offers a rich ecosystem with three distinct network interaction domains. These domains serve specialized purposes and contribute to the robust functionality of Waku, forming its foundation.","sidebar":"main"},"overview/concepts/peer-discovery":{"id":"overview/concepts/peer-discovery","title":"Peer Discovery","description":"When initializing a Waku node, it must connect with other peers to enable message sending, receiving, and retrieval. To achieve this, a discovery mechanism is employed to locate other peers in the network. This process is known as bootstrapping.","sidebar":"main"},"overview/concepts/peer-discovery/discv5":{"id":"overview/concepts/peer-discovery/discv5","title":"Discv5","description":"Discv5 is a decentralized and efficient peer discovery mechanism for the Waku Network. It uses a Distributed Hash Table (DHT) for storing ENR records, providing resistance to censorship. Discv5 offers a global view of participating nodes, enabling random sampling for load distribution. It uses bootstrap nodes as an entry point to the network, providing randomized sets of nodes for mesh expansion. Please refer to the Discv5 specification to learn more.","sidebar":"main"},"overview/concepts/peer-discovery/dns-discovery":{"id":"overview/concepts/peer-discovery/dns-discovery","title":"DNS Discovery","description":"Built upon the foundation of EIP-1459: Node Discovery via DNS, DNS Discovery allows the retrieval of an ENR tree from the TXT field of a domain name. This innovative approach enables the storage of essential node connection details, including IP, port, and multiaddr, using the standardized ENR format.","sidebar":"main"},"overview/concepts/peer-discovery/peer-exchange":{"id":"overview/concepts/peer-discovery/peer-exchange","title":"Peer Exchange","description":"The primary objective of this protocol is to facilitate peer connectivity for resource-limited devices. The peer exchange protocol enables lightweight nodes to request peers from other nodes within the network. Light nodes can bootstrap and expand their mesh independently without relying on Discv5. Please refer to the Peer Exchange specification to learn more.","sidebar":"main"},"overview/concepts/peer-discovery/predefined-nodes":{"id":"overview/concepts/peer-discovery/predefined-nodes","title":"Predefined Nodes","description":"Waku applications have the flexibility to embed bootstrap node addresses directly into their codebase. Developers can use either the predefined nodes by Status or run a node per their preference.","sidebar":"main"},"overview/concepts/protocols":{"id":"overview/concepts/protocols","title":"Protocols","description":"Waku takes a modular approach, providing a range of protocols that enable applications to control the trade-offs involved in the Anonymity Trilemma. This flexibility empowers applications to make informed choices regarding the desired balance between anonymity, scalability, and latency. Here are the main protocols provided by Waku:","sidebar":"main"},"overview/concepts/transports":{"id":"overview/concepts/transports","title":"Transports","description":"Transports help move data packets across a network by establishing connections between peers. They define the rules and protocols to ensure efficient network transmission, routing, and data delivery.","sidebar":"main"},"overview/history":{"id":"overview/history","title":"History of Waku","description":"Waku v1 was a fork of Whisper with some added tweaks for efficiency. Waku v2 introduces a fully revamped suite of protocols designed to address the goals set out previously.","sidebar":"main"},"overview/index":{"id":"overview/index","title":"What is Waku?","description":"Waku has risks and limitations as it is still developing and preparing for extensive adoption. However, it is already demonstrating its capabilities by powering various applications. Join our community to stay updated on our progress.","sidebar":"main"},"overview/reference/glossary":{"id":"overview/reference/glossary","title":"Glossary","description":"Definitions and usage of the terminology used in the Waku ecosystem.","sidebar":"main"},"overview/reference/research-in-progress":{"id":"overview/reference/research-in-progress","title":"Research in Progress","description":"The following features are currently experimental and under research and initial implementation:","sidebar":"main"},"overview/reference/security-features":{"id":"overview/reference/security-features","title":"Security Features","description":"Waku\'s protocol layers offer different services and security considerations, shaping the overall security of Waku. We document the security models in the RFCs of the protocols, aiming to provide transparent and open-source references. This empowers Waku users to understand each protocol\'s security guarantees and limitations.","sidebar":"main"},"overview/reference/waku-vs-libp2p":{"id":"overview/reference/waku-vs-libp2p","title":"Comparing Waku and libp2p","description":"Since Waku is built on top of libp2p, they share a lot of concepts and terminologies between them. However, there are key differences between them that are worth noting.","sidebar":"main"},"overview/use-cases":{"id":"overview/use-cases","title":"Use Cases","description":"Waku is a decentralized communication network, facilitating secure and private person-to-person and machine-to-machine communication without a central authority. It supports various use cases, including but not limited to:","sidebar":"main"},"overview/why-waku":{"id":"overview/why-waku","title":"Why Waku?","description":"Communication in the present day is heavily influenced by third-party intervention, ranging from censorship and deplatforming to intermediaries that seek to profit from rent and the misuse of data in the surveillance economy.","sidebar":"main"},"powered-by-waku":{"id":"powered-by-waku","title":"Powered by Waku","description":"Leap into the future with Waku! Revolutionize communication, collaboration, and connectivity. Join us and create something extraordinary!","sidebar":"community"},"presentations":{"id":"presentations","title":"Watch Our Presentations","description":"Using ZKP for Better p2p Messaging with Waku","sidebar":"community"}}}')}}]);